sys_script_include.script:
var PowershellProbeAjax = Class.create();
PowershellProbeAjax.prototype = Object.extendsObject(AbstractAjaxProcessor, {
    execute: function() {
		var mid = this.getParameter('sysparm_mid');
		var midScript = this.getParameter('sysparm_mid_script');
		var server = this.getParameter('sysparm_server');
		var script = this.getParameter('sysparm_script');
		var pb = new PowershellProbe(mid, server, midScript);
		pb.setScript(script);
		var response = pb.execute(true);
		return this.getResponseXmlString(response);
    },

	getResponseXmlString: function(response){
		var ret = new XMLDocument("<response/>");
		if(response.output && response.output != "null") {
			ret.createElement("output",GlideStringUtil.base64Encode(response.output));
		}
		if( response.error && response.error != "null"){
			ret.createElement("error", GlideStringUtil.base64Encode(response.error));
		}
		return ret.toString();
	},

    type: 'PowershellProbeAjax'
});
